import './App.css';
import{useState} from 'react';

function App() {
  const [cal, setCal] = useState('');
  const [result, setResult] = useState('');
  const operator = ['/', '*', '+', '-', '.'];

  const updateCal = (value) =>{
    if(operator.includes(value) && cal === '' || operator.includes(value) && operator.includes(cal.slice(-1))){
      return;
    }
    setCal(cal + value);

    if(!operator.includes(value)){
      setResult(eval(cal + value).toString())
    }
  };

  const createDigits = ()=>{
    const digits = [];
    for(let i=1; i<10; i++){
        digits.push(
          <button onClick={() => updateCal(i.toString())} key={i}>{i}</button>
        )
    }
    return digits;
  }
  return (
    <div className="App">
      <div className='calculator'>
        <div className='display'>
          { result? <span>(0)</span>: ''} {cal || '0'}
        </div>
        <div className='operators'>
            <button onClick = { ()=> updateCal ('/')}>/</button>
            <button onClick = { ()=> updateCal ('*')}>*</button>
            <button onClick = { ()=> updateCal ('+')}>+</button>
            <button onClick = { ()=> updateCal ('-')}>-</button>
            <button>DEL</button>
        </div>
        <div className='digits'>
            {createDigits()}
            <button onClick = { ()=> updateCal ('0')}>0</button>
            <button onClick = { ()=> updateCal ('.')}>.</button>
            <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
